import axios from 'axios';
import REPO_ACTIONS from '../store/repo/repo.actionTypes';
import {fetchRepos,fetchRepoSucess,fetchRepoFailure} from '../store/repo/repo.action';
 export async function fetchUserRepoTest(name) {
  let response = await fetch(`https://api.github.com/users/${name}/repos`);
  let data = await response.json();
  console.log(data);
  loadReposSuccess(data);
  // dispatch(getUser('data'));
  //let repos = getRepos(data.repos_url);

  return data;
} 
