//import {fetchProductsPending, fetchProductsSuccess, fetchProductsError} from 'actions';


function fetchUserRepo(user) {
    return dispatch => {
      //  dispatch(fetchProductsPending());
        fetch(`https://api.github.com/users/${user}/repos`)
        .then(res => res.json())
        .then(res => {
            if(res.error) {
                throw(res.error);
            }
            dispatch(fetchRepoSucess(res.products));
            return res.products;
        })
        .catch(error => {
            dispatch(fetchRepoFailure(error));
        })
    }
}

export default fetchUserRepo;
